    format_date_to_full(aDate, aTime) {
      //excpected input: 14/04/2023
      var year = aDate.substring(6, 10);
      var month = aDate.substring(3, 5);
      var day = aDate.substring(0, 2);
      return year + '-' + month + '-' + day + " " + aTime;
