    getTrips() {
      this.panelClass = 'closedPanel';
      this.isLoading = true;
      const fromDateFull = this.format_date_to_full(this.fromDate, '00:00:00');
      const toDateFull = this.format_date_to_full(this.toDate, '23:59:59');
      const sessionId = localStorage.getItem("token");
      const DateFrom = fromDateFull;
      const DateTo = toDateFull;
      const UserId = localStorage.getItem("userId");
      const UserType = this.userType;
      const baseUrl = localStorage.getItem("_rootRestUrl");
      let formData = JSON.stringify({
        sessionId: sessionId,
        DateFrom: DateFrom,
        DateTo: DateTo,
        UserId: UserId,
        UserType: UserType
      });
      let config = {
        headers: {
          "Content-Type": "application/json"
        }
      };
      axios.get(baseUrl + "/api/v1/Trip/Trips/?sessionId=" + sessionId + "&DateFrom=" + DateFrom + "&DateTo=" + DateTo + "&UserId=" + UserId + "&UserType=" + UserType + "", formData, config).then(response => {
