          localStorage.removeItem("selectedProjectImage");
          localStorage.removeItem("selectedProjectNumber");
          localStorage.removeItem("selectedProjectPublishDate");
          localStorage.removeItem("selectedProjectTitle");
          localStorage.removeItem("selectedProjectValidUntil");
          localStorage.removeItem("appType");
          document.location = '/';
        }

        console.log(error);
      });
