import moment from "moment";
import $ from "jquery";
import axios from "axios";
/*   allCompanies: {"lsdkfn309784bv":"DFM System","hrfgh46h":"Computech"}, ,*/

export default {
  props: ["projectKey", "projectTitle", "projectNumber", "emitSearch"],

  data() {
    return {
      selectedCompanyKey: null,
      selectedCompanyName: null,
      detailsAreVisible: false,
      detailsAreAppsVisible: false,
      isLoading: false,
      projects: [],
      allCompaniesForDropdown: [],
      //json from rest api
      allCompanies: [{
        value: "asd23jhbrkjb",
        label: "DFM Systems"
      }, {
        value: "sdfssfgh45hd",
        label: "Computech"
      }],
      error: null,
      NewProjectTitle: "",
      NewProjectNumber: "",
      NewProjectImage: null,
      NewProjectImageBase64: "",
      //just to display new uploaded image
      NewExpiryDate: "",
      NewContractor: "",
      EditProjectTitle: "",
      EditProjectNumber: "",
      EditProjectImage: "",
      EditExpiryDate: "",
      alertNewProjectTitle: "",
      alertNewProjectNumber: "",
      alertNewExpiryDate: "",
      alertNewProjectImage: "",
      alertNewCompany: "",
      alertEditProjectTitle: "",
      alertEditProjectNumber: "",
      alertEditExpiryDate: "",
      alertEditProjectImage: "",
      imagePlaceholder: "https://via.placeholder.com/360x200.png?text=Project%20Image",
      tmpBase64: "",
      formError: false,
      createdProject: false,
      block: "",
      file: "",
      defaultCard: this._rootDefaultProjectCard
    };
  },

  methods: {
    async getCompanies() {
      if (this.$store.getters.isAuthenticated) {
        // var allOk = false;
        var ErrorCode = "";
        let config = {
          headers: {
            "Content-Type": "application/json"
          }
        };
        const token = localStorage.getItem("token");
        const baseUrl = localStorage.getItem("_rootRestUrl");
        let formData = JSON.stringify({
          token: token
        });
        await axios.post(baseUrl + "/Company/GetCompaniesForDropdown", formData, config).then(response => {
          console.log(response); //  allOk = true;

          this.allCompaniesForDropdown = response.data; //   ErrorCode = 200;
        }).catch(function (error) {
          console.table(error.response);
          alert("ERROR " + error.response.status + ": " + error.response.data);
          ErrorCode = error.response.status;
        });
      } else {
        this.gotoHomePage();
      }
    },

    /// GET project list
    async getProjectList() {
      this.isLoading = true;

      if (this.$store.getters.isAuthenticated) {
        const token = localStorage.getItem("token"); //User token !!

        var allOk = false;
        var ErrorCode = "";
        let config = {
          headers: {
            "Content-Type": "application/json"
          }
        };
        let formData = JSON.stringify({
          token: token
        });
        const baseUrl = localStorage.getItem("_rootRestUrl");
        await axios.post(baseUrl + "/Project/GetProjects", formData, config).then(response => {
          console.log(response);
          allOk = true;
          this.projects = response.data;
          ErrorCode = 200;
        }).catch(function (error) {
          console.table(error.response);
          alert("ERROR " + error.response.status + ": " + error.response.data);
          ErrorCode = error.response.status;
        });

        if (allOk) {
          /// add SERVER/IMAGES to image name in json:
          for (const key in this.projects) {
            this.projects[key]["imageName"] = this._rootRestUrl + "/images/" + this.projects[key]["imageName"];
          }

          this.isLoading = false;
          this._allProjectsList = this.projects;
        }

        this.isLoading = false;

        if (ErrorCode == 400) {
          this.gotoHomePage(); //token expired
        }
      } else {
        this.gotoHomePage();
      }
    },

    gotoHomePage() {
      this.isLoading = false;
      this.$store.dispatch("logout");
      this.$router.push("/");
    },

    ////
    handleFileUpload(event) {
      this.selectedFile = event.target.files[0];
      this.file = this.selectedFile;
      this.convertToBase64();
    },

    convertToBase64() {
      const selectedFile = this.file; //   if (selectedFile.length > 0) {

      var fileToLoad = selectedFile;
      var fileReader = new FileReader();
      var base64;

      fileReader.onload = function (fileLoadedEvent) {
        base64 = fileLoadedEvent.target.result;
        base64 = base64.split(","); // remove type from base64

        this.tmpBase64 = base64;
        $("#newImage").attr("src", base64);
      };

      fileReader.readAsDataURL(fileToLoad); //        }
      //   this.EditProjectImageBase64 = localStorage.removeItem("tmpBase64");
    },

    selectChange(option) {
      this.selectedCompanyKey = option.value;
      this.selectedCompanyName = option.label;
    },

    handleTagCreate(option) {
      const randomNumber = Math.floor(Math.random() * 1000000);
      option.value = "NEW--" + randomNumber;
      this.allCompanies.push({
        value: "NEW--" + randomNumber,
        label: option.label
      });
      return option;
    },

    projectSearch(what) {
      //TODO: functon for searching Projects
      if (what.length > 2) {
        for (let element of document.getElementsByClassName("cardsDisplay")) {
          element.classList.remove("show");
          element.style.display = "none";
        }

        this.projects.forEach(value => {
          const dfmOnlinNumber = value["projectNumber"].toString().toUpperCase();
          const dfmTitle = value["projectName"].toString().toUpperCase();
          const dfmCompany = value["contractor"].toString().toUpperCase();
          what = what.toUpperCase();

          if (dfmOnlinNumber.includes(what) || dfmTitle.includes(what) || dfmCompany.includes(what)) {
            //document.getElementById("ProjectCard-" + value["key"]).className += " show";
            document.getElementById("ProjectCard-" + value["key"]).style.display = "block";
          } else {
            document.getElementById("ProjectCard-" + value["key"]).style.display = "none";
          }
        });
      } else {
        for (let element of document.getElementsByClassName("cardsDisplay")) {
          //element.classList.add("show");
          element.style.display = "block";
        }
      }
    },

    toggleDetails() {
      this.detailsAreVisible = !this.detailsAreVisible;
    },

    toggleDetailsApp() {
      this.detailsAreAppsVisible = !this.detailsAreAppsVisible;
    },

    hideOpenApps() {
      this.detailsAreAppsVisible = false;
    },

    format_date(value, pattern) {
      if (value) {
        return moment(String(value)).format(pattern);
      }
    },

    format_date_reverse(value) {
      //excpected input: 	24/09/2022
      try {
        const day = value.substring(0, 2);
        const month = value.substring(3, 5);
        const year = value.substring(6, 10);
        return year + "-" + month + "-" + day;
      } catch (e) {
        return "undefined";
      }
    },

    todayDate() {
      var today = moment().format("YYYY-MM-DD 00:00:00");
      return today;
    },

    changindDate() {
      this.NewExpiryDate = this.format_date(this.NewExpiryDate, "DD/MM/YYYY");
      this.alertNewExpiryDate = "";
    },

    beforeOpenaddModal() {
      this.getCompanies(); //clear all inpute fields

      this.$refs.file = "";
      $("#newImage").attr("src", "https://via.placeholder.com/360x200.png?text=Project%20Image");
      this.NewExpiryDate = "";
      this.NewProjectTitle = "";
      this.NewProjectNumber = "";
    },

    activateLoader() {},

    async submitForm() {
      this.formError = false;

      if (this.NewProjectTitle == "") {
        this.alertNewProjectTitle = "Please enter Project title";
        this.formError = true;
      }

      if (this.NewProjectNumber == "") {
        this.alertNewProjectNumber = "Please enter DFM Number";
        this.formError = true;
      }

      if (this.NewExpiryDate == "") {
        this.alertNewExpiryDate = "Please select expiry date";
        this.formError = true;
      }

      if (this.formError == false) {
        //create project ! for now just push in array
        this.isLoading = true;
        var allOk = false;
        var ErrorCode = "";
        const baseUrl = localStorage.getItem("_rootRestUrl");
        const token = localStorage.getItem("token"); //User token !!

        const formData = new FormData();
        formData.append("Token", token);
        formData.append("ProjectNumber", this.NewProjectNumber);
        formData.append("ProjectName", this.NewProjectTitle);
        formData.append("ContractorKey", this.selectedCompanyKey);
        formData.append("ContractorName", this.selectedCompanyName);
        formData.append("ActiveUntil", this.format_date_reverse(this.NewExpiryDate) + " 00:00:00");

        if (this.selectedFile) {
          formData.append("image", this.selectedFile, this.selectedFile.name);
        }

        let config = {
          headers: {
            "Content-Type": "multipart/form-data"
          }
        };
        await axios.post(baseUrl + "/Project/CreateProject", formData, config).then(response => {
          console.log(response);
          allOk = true;
          ErrorCode = 200;
        }).catch(function (error) {
          console.table(error.response);
          ErrorCode = error.response.status;
          allOk = false;
          alert("ERROR " + ErrorCode + ": " + error.response.data);
        });

        if (allOk == true) {
          this.closeModal("closeaddModal"); //close login modal after submit;

          document.getElementById("btnOpenMo").click();
          this.getProjectList();
          this.isLoading = false;
        }

        this.isLoading = false;
        this.closeModal("closeaddModal"); //close login modal after submit;
      }
    },

    editProject(projectKey) {
      const thisProject = this.projects.find(project => project.key === projectKey);
      document.getElementById("editModal").click();
      alert(thisProject.projectName);
    },

    KeyDown(field) {
      if (field === "NewProjectTitle") {
        this.alertNewProjectTitle = "";
      } else if (field === "NewProjectNumber") {
        this.alertNewProjectNumber = "";
      }
    },

    closeModal(id) {
      document.getElementById(id).click();
    },

    selectedProject(projectKey, appType) {
      //store selected project details in localstorage:
      localStorage.setItem("projectKey", projectKey);
      localStorage.setItem("appType", appType);
      const thisProject = this.projects.find(project => project.key === projectKey);
      localStorage.setItem("selectedProjectTitle", thisProject.projectName);
      localStorage.setItem("selectedProjectNumber", thisProject.projectNumber);
      localStorage.setItem("selectedProjectContractor", thisProject.contractor);
      localStorage.setItem("selectedProjectValidUntil", thisProject.activeUntil);
      localStorage.setItem("selectedProjectPublishDate", thisProject.created);

      if (thisProject.imageName == "" || thisProject.imageName == null) {
        localStorage.setItem("selectedProjectImage", this.defaultCard);
      } else {
        localStorage.setItem("selectedProjectImage", thisProject.imageName);
      }

      localStorage.setItem("selectedProjectType", appType);
    }

  },
  emits: {
    showSearchbar: false,
    appType: ""
  },

  created() {
    localStorage.removeItem("appType");
    localStorage.removeItem("selectedProjectType");
    localStorage.removeItem("selectedProjectTitle");
    localStorage.removeItem("selectedProjectNumber");
    localStorage.removeItem("selectedProjectContractor");
    localStorage.removeItem("selectedProjectValidUntil"), localStorage.removeItem("selectedProjectPublishDate"), localStorage.removeItem("selectedProjectImage");
    localStorage.removeItem("selectedProjectType");
    this.$emit("appType", "");
  },

  mounted() {
    this.getProjectList();
    this.$emit("showSearchbar", true);
    localStorage.setItem("selectedProjectType", "");
  },

  provide() {
    return {
      projects: this.projects
    };
  }

};
