let timer; // for holding timer function for auto logout if session expire



export default {
  async login(context, payload) {
    return context.dispatch("auth", {
      ...payload,
    });
  },

  async auth(context, payload) {
    //const token = context.rootGetters.token; //User token !!

    const baseUrl = localStorage.getItem("_rootRestUrl");
    const response = await fetch(baseUrl+"/Authenthication/Login", {
      method: "POST",

      headers: {
        "Content-Type":"application/json"
      },
      body: JSON.stringify({
        username: payload.username,
        password: payload.password,
      }),
    });

   /* if (response.status==404) {  
       
        alert("Wrong username or password!");
       // parent.isLoading = false;
    }*/
    if (response.status!=200) {
       const responseData2 = await response.text();

        // console.log(responseData);
        const error = new Error(
            responseData2
        );
        throw error;
      }



    const responseData = await response.json();


    //expecting in json recive 'exipresIn' in minutes! how long will session exists
    const expiresIn = responseData.validFor * 1000 * 60;

    //auto logout if token expires
    timer = setTimeout(function () {
      context.dispatch("autoLogout");
    }, expiresIn);

    //store login details in local storeage so that on refresh page app remember loged user!

    localStorage.setItem("token", responseData.key);
    localStorage.setItem("userId", responseData.userId);
    localStorage.setItem("tokenExpiration", responseData.validUntil);

    //data needed from login json:

    //localStorage.setItem('userFullname',payload.username);

    localStorage.setItem("expiresIn", expiresIn);

    context.commit("setUser", {
      token: responseData.key,
      userId: responseData.userId,
      userFullname: "Full name",
    });
  },

  logout(context) {
    localStorage.removeItem("token");
    localStorage.removeItem("userId");
    localStorage.removeItem("userFullname");
    localStorage.removeItem("tokenExpiration");
    localStorage.removeItem("expiresIn");
    localStorage.removeItem("_rootRestUrl");
    localStorage.removeItem("selectedProjectType");
    localStorage.removeItem("projectKey");
    localStorage.removeItem("selectedProjectContractor");
    localStorage.removeItem("selectedProjectImage");
    localStorage.removeItem("selectedProjectNumber");
    localStorage.removeItem("selectedProjectPublishDate");
    localStorage.removeItem("selectedProjectTitle");
    localStorage.removeItem("selectedProjectValidUntil");
    localStorage.removeItem("appType");

    clearTimeout(timer);

    context.commit("setUser", {
      token: null,
      userId: null,
      userFullname: null,
      tokenExpiration: null,
    });
  },

  //try to login if local storage containt login details
  autoLogin(context) {

    alert("autologin")
    const token = localStorage.getItem("token");
    const userId = localStorage.getItem("userId");
    const userFullname = localStorage.getItem("userFullname");
    const tokenExpiration = localStorage.getItem("tokenExpiration");
    const willExpire = +tokenExpiration - new Date().getTime();
    const expiresIn = localStorage.getItem("expiresIn");

    if (willExpire < 0) {
      //if token expires do nothing
      return;
    }

    //if is token ok then we must extend life of the token for the next expiration time

    const expirationDate = new Date().getTime() + expiresIn;
    localStorage.setItem("tokenExpiration", expirationDate);

    //set new timer
    timer = setTimeout(function () {
      context.dispatch("autoLogout");
    }, expiresIn);

    //login "again" this user
    if (token && userId && userFullname) {
      context.commit("setUser", {
        token: token,
        userId: userId,
        userFullname: userFullname,
      });
    }
  },
  autoLogout(context) {
    context.dispatch("logout");
    context.commit("setAutoLogout");
  },
};
