import { createApp, defineAsyncComponent } from "vue";
import { createRouter, createWebHistory } from "vue-router";

import App from "./App.vue";

// pages
//import LoginPage from "./components/LoginPage.vue";
//import PagesHolder from './components/PagesHolder.vue';

//async pages, loaded when needed! not in advance
const LoginPage = defineAsyncComponent(() => import('./components/pages/LoginPage.vue'));
const PagesHolder = defineAsyncComponent(() => import('./components/pages/PagesHolder.vue'));
const ProjectsCards = defineAsyncComponent(() => import('./components/pages/ProjectsCards.vue'));
const ManageUsers = defineAsyncComponent(() => import('./components/pages/ManageUsers.vue'));

import store from './store/index.js';

//includable parts of the page
import TopBar from './components/include/TopBar.vue';
import SideBar from './components/include/SideBar.vue';


/// some UI
import VersionInfo from './components/ui/VersionInfo.vue';
import BaseDialog from './components/ui/BaseDialog.vue';
import BaseSpinner from './components/ui/BaseSpinner.vue';


const router = createRouter({

  history: createWebHistory(''),
  routes: [
    { path: "/", component: LoginPage },
    { path: "/pages", 
      component: PagesHolder ,
        children:[
          {
            path: "projectList",
            component: ProjectsCards
          },
          {
            path: "manageUsers",
            component : ManageUsers,

          }
        ]
    },

  ],
});

const app = createApp(App);

//global variables
   app.config.globalProperties._rootAppTitle = 'DFM Live';
   app.config.globalProperties._rootLoginBackground = '/images/login-background.jpg';
   app.config.globalProperties._rootLoginLogo = '/images/login-logo.png';
   app.config.globalProperties._rootLoginLogoWhite = '/images/login-logo-white.png';
   app.config.globalProperties._rootlinkToHelpFile = 'https://safetyfile.onlinedfm.com/Help/UserguideV2.0.1.84.pdf';

// -------------------

app.use(router);
app.use(store);


app.component('top-bar', TopBar);
app.component('side-bar', SideBar);

app.component('login-page', LoginPage);

app.component('version-info', VersionInfo);
app.component('base-spinner', BaseSpinner);
app.component('base-dialog', BaseDialog);


app.mount("#app");
