/*
//visual settings:
var onLoadSidebarWidth = 200;
var breakPointForSmallScreen = 750;
var minWSidebarWidth = 50;

var breakPointHideSidebarDescriptions = 150;
var maxPercentageSidebarWidth = 30;
var onLoadContentWidth = window.innerWidth - onLoadSidebarWidth;
*/
export default {
  props: ["emitSearch"],

  data() {
    return {
      TopBackgroundStyle: "red",
      showSearch: false,
      MainTitleText: "sds",
      //this._rootAppTitle,
      showMenu: true,
      // for visual settings:
      padding0: "12px",
      menuMinSize: 50,
      breakPointHideSidebarDescriptions: 170,
      // when to hide menu description and show only icon
      dividerPosition: 210,
      //px default width of the left sidebar!
      contentWidth: window.innerWidth,
      //px width of the main content
      isActive: true,
      //in start visible sidebar icon descriptions
      isCentered: false,
      //sidebar icons on left at page load
      iconCloseOpen: "&#9776;",
      iconCloseOpen2: "&#9776;"
    };
  },

  methods: {
    /// ------------------------------
    /// resizable left sidebar!
    handleDragging(e) {
      const percentage = e.pageX / window.innerWidth * 100;

      if (localStorage.getItem("dividerPosition")) {
        this.dividerPosition = localStorage.getItem("dividerPosition");
      }

      if (e.pageX + 1 > this.menuMinSize && percentage <= 30) {
        //only resizable if width of the left sidebar is in this percentage range
        this.dividerPosition = percentage / 100 * window.innerWidth;
      }

      this.contentWidth = window.innerWidth - this.dividerPosition; // on small screens hide sidebar menu descriptions

      if (e.pageX < this.breakPointHideSidebarDescriptions) {
        this.isActive = false;
        this.iconCloseOpen = "&#9776;"; //    this.isCentered = true;
      } else {
        this.isActive = true;
        this.iconCloseOpen = "&#9776;";
        this.isCentered = false;
      }

      localStorage.setItem("dividerPosition", this.dividerPosition); // -------------------
    },

    startDragging() {
      document.addEventListener("mousemove", this.handleDragging);
    },

    endDragging() {
      document.removeEventListener("mousemove", this.handleDragging);
    },

    setToFullScreenWidth() {
      this.contentWidth = window.innerWidth - this.menuMinSize; // read if we have position set

      var isSideBarOpen = localStorage.getItem("isSideBarOpen");

      if (localStorage.getItem("dividerPosition")) {
        this.dividerPosition = localStorage.getItem("dividerPosition");
      } else {
        localStorage.setItem("dividerPosition", 200);
        this.dividerPosition = 200;
      }

      if (localStorage.getItem("isSideBarOpen")) {
        //there is something in local storage for open/close menu
        if (isSideBarOpen === "true") {
          this.iconCloseOpen = "&#9776;";
          this.isActive = false;
          this.sidebarColapseOnLoad();
        } else {
          this.iconCloseOpen = "&times;";
          this.isActive = false;
          this.sidebarColapseOnLoad();
        }
      } else {
        //empty localstorage fro menu open
        if (this.iconCloseOpen === "&#9776;") {
          localStorage.setItem("isSideBarOpen", true);
          this.sidebarColapseOnLoad();
        } else {
          localStorage.setItem("isSideBarOpen", false);
          this.sidebarColapseOnLoad();
        }
      }
    },

    getDimensions() {
      //on window resize
      if (window.innerWidth > 442) {
        this.dividerPosition = 200;
        this.contentWidth = window.innerWidth - this.dividerPosition;
        this.isActive = true;
        this.iconCloseOpen = "&times;";
        this.isCentered = false;
      } else {
        this.dividerPosition = this.menuMinSize;
        this.isActive = false;
        this.iconCloseOpen = "&#9776;"; //  this.isCentered = true;
      }

      if (window.innerWidth < 500) {
        this.dividerPosition = this.menuMinSize;
        this.contentWidth = window.innerWidth - this.dividerPosition;
        this.isActive = false;
        this.iconCloseOpen = "&#9776;"; //   this.isCentered = true;
      }
    },

    sidebarColapse() {
      if (this.iconCloseOpen === "&#9776;") {
        //we want to open it
        if (localStorage.getItem("dividerPosition")) {
          this.dividerPosition = localStorage.getItem("dividerPosition");
        } else {
          this.dividerPosition = 200;
        }

        if (this.dividerPosition < 200) {
          this.dividerPosition = 200;
        }

        this.contentWidth = window.innerWidth - this.dividerPosition;
        this.isActive = true;
        this.isCentered = false;
        this.iconCloseOpen = "&times;";
        localStorage.setItem("isSideBarOpen", true);
      } else {
        //we wnat to close it
        this.dividerPosition = this.menuMinSize;
        this.contentWidth = window.innerWidth - this.dividerPosition;
        this.isActive = false; //     this.isCentered = true;

        this.iconCloseOpen = "&#9776;";
        localStorage.setItem("isSideBarOpen", false);
      }
    },

    sidebarColapseOnLoad() {
      if (this.iconCloseOpen === "&#9776;") {
        //we want to open it
        if (localStorage.getItem("dividerPosition")) {
          this.dividerPosition = localStorage.getItem("dividerPosition");
        } else {
          this.dividerPosition = 200;
        }
        /*if( this.dividerPosition < 200){
          this.dividerPosition = 200;
        }*/


        this.contentWidth = window.innerWidth - this.dividerPosition;
        this.isActive = true;
        this.isCentered = false;
        this.iconCloseOpen = "&times;";
        localStorage.setItem("isSideBarOpen", true);
      } else {
        //we wnat to close it
        this.dividerPosition = this.menuMinSize;
        this.contentWidth = window.innerWidth - this.dividerPosition;
        this.isActive = false; //  this.isCentered = true;

        this.iconCloseOpen = "&#9776;";
        localStorage.setItem("isSideBarOpen", false);
      }

      if (this.dividerPosition < this.breakPointHideSidebarDescriptions) {
        this.isActive = false;
        this.iconCloseOpen = "&#9776;"; //     this.isCentered = true;
      }
    },

    // ----------- end resizable left menu -------
    manualLogout() {
      this.$store.dispatch("logout");
      this.showMenu = false;
      this.$router.replace("/");
    },

    showSeachbar(what) {
      this.showSearch = what;
    },

    emitedSearch() {}

  },
  emits: ["emitSearch"],

  mounted() {
    window.addEventListener("resize", this.getDimensions);
  },

  unmounted() {
    window.removeEventListener("resize", this.getDimensions);
  },

  created() {
    // this.rView.projectSearch("hehehe");
    this.setToFullScreenWidth(); //if user is no loged in then redirect to login page:

    if (!this.$store.getters.isAuthenticated) {
      this.$router.replace("/");
    } //check if user is loggen in


    this.$store.dispatch("autoLogin"); //on create (on load)
    // this.projects = projectsObject; // parsed json
    //hidden sidebar on load if small screen

    if (window.innerWidth < 750) {// this.dividerPosition = this.menuMinSize;
    }
