let timer; // for holding timer function for auto logout if session expire

export default {
    async login(context,payload) {
          return  context.dispatch('auth',{
                ...payload
            });
    }, 
    
    async  auth(context,payload){

        const token = context.rootGetters.token; //User token !!

        //const response = await fetch(_rootRestUrl+'/Authenthication/Login',{
        const response = await fetch('https://www.it-b.hr/computech.php?auth='+token,{
               method: 'POST',
               body: JSON.stringify({
                   username: payload.username,
                   password: payload.password,
                   userFullname: payload.userFullname,
                   returnSecureToken : true
               })
              
   
          });
                 
          const responseData = await response.json();
   
          if(!response.ok){
              // console.log(responseData);
               const error = new Error(responseData.message || 'Failed to authenticate.');
               throw error;
          }

          //expecting in json recive 'exipresIn' in minutes! how long will session exists
          const expiresIn = +responseData[0].expiresIn * 1000 * 60;
          const expirationDate = new Date().getTime() +  expiresIn;
   
          //auto logout if token expires
          timer = setTimeout(function(){
            context.dispatch('autoLogout');
          },expiresIn)

          //store login details in local storeage so that on refresh page app remember loged user!
          localStorage.setItem('token',responseData[0].token);
          localStorage.setItem('userId',responseData[0].userId);
          localStorage.setItem('userFullname',responseData[0].userFullname);
          localStorage.setItem('tokenExpiration',expirationDate);
          localStorage.setItem('expiresIn',expiresIn);

          context.commit('setUser',{
                  token: responseData[0].token,
                  userId : responseData[0].userId,
                  userFullname: responseData[0].userFullname
                 
   
           });
   
    },
    
    logout(context){

        localStorage.removeItem('token');
        localStorage.removeItem('userId');
        localStorage.removeItem('userFullname');
        localStorage.removeItem('tokenExpiration'); 
        localStorage.removeItem('expiresIn'); 
        

        clearTimeout(timer);

        context.commit('setUser', {
            token : null,
            userId : null,
            userFullname : null,
            tokenExpiration : null
        })

        

    },

    //try to login if local storage containt login details
    autoLogin(context){
       
        const token = localStorage.getItem('token');
        const userId = localStorage.getItem('userId');
        const userFullname = localStorage.getItem('userFullname');
        const tokenExpiration = localStorage.getItem('tokenExpiration');
        const willExpire = +tokenExpiration - new Date().getTime();
        const expiresIn =localStorage.getItem('expiresIn');

        if(willExpire < 0){
            //if token expires do nothing
            return;
        }
       
        //if is token ok then we must extend life of the token for the next expiration time
                            
        const expirationDate = new Date().getTime() + expiresIn ;
        localStorage.setItem('tokenExpiration',expirationDate);

       
        //set new timer
        timer = setTimeout(function(){
            context.dispatch('autoLogout');
        }, expiresIn)

        //login "again" this user
        if (token && userId && userFullname){
            context.commit('setUser',{
                  token: token,
                  userId : userId,
                  userFullname: userFullname
                  
            });
        }

    },
    autoLogout(context){
        context.dispatch('logout');
        context.commit('setAutoLogout');
            
   
    }



}
