    async insertUserToDatabase() {
      this.isLoading = true;
      const toDateFull = this.format_date(moment(this.newUserActiveUntil, 'DD/MM/YYYY HH:mm'), 'YYYY-MM-DD');
      var type = '';

      if (this.newUserType == 'Admin') {
        type = 0;
      } else {
        type = 1;
      }

      var language = '';

      if (this.newUserLanguage == 'Lietuvių') {
        language = "LT";
      } else if (this.newUserLanguage == 'English') {
        language = 'EN';
      }

      var active = '';

      if (this.newUserIsActive == 'Yes') {
        active = true;
      } else if (this.newUserIsActive == 'No') {
        active = false;
      }

      const sessionId = localStorage.getItem("token");
      const baseUrl = localStorage.getItem("_rootRestUrl");
      let formData = JSON.stringify({
        "sessionId": sessionId,
        "usersToInsert": [{
          "name": this.newUserName,
          "surname": this.newUserSurname,
          "email": this.newUserEmail,
          "password": this.newUserPassword,
          "language": language,
          "activeUntil": toDateFull,
          "isActive": active,
          "type": type
        }]
