    async getAllUsers() {
      this.isLoading = true;
      const sessionId = localStorage.getItem("token");
      const baseUrl = localStorage.getItem("_rootRestUrl");
      let formData = JSON.stringify({
        sessionId: sessionId
      });
      let config = {
        headers: {
          "Content-Type": "application/json"
        }
      };
      await axios.get(baseUrl + "/api/v1/User/Users/?sessionId=" + sessionId + "", formData, config).then(response => {
        this.allUsers = response.data;
        this.correctData(); // correct some true/false and 1/0 tu values needed for dataTable to be sortable and filtered correctly

        this.reorganizePagination();
        this.isLoading = false;
      }).catch(function (error) {
        // handle error
        if (error.response) {
          if (error.response.status == '401') {
            //not authorized, token expires
            //hm, I don't know why this three next lines not work :( so I made it "manual logout"
            // this.isLoading = false;  
            // this.$store.dispatch("logout");
            // this.$router.replace("/");
            localStorage.removeItem("token");
            localStorage.removeItem("userId");
            localStorage.removeItem("userFullname");
            localStorage.removeItem("userType");
            localStorage.removeItem("tokenExpiration");
            localStorage.removeItem("expiresIn");
            document.location = '/';
          }
        }

        console.table(error);
      });
      this.isLoading = false;
