import Dropdown from 'primevue/dropdown';
import axios from "axios";
import $ from "jquery";
import { FilterMatchMode, FilterOperator } from 'primevue/api'; // for filtering

export default {
  props: ["systemId"],
  components: {
    DataTable,
    Column,
    InputText,
    //Checkbox,
    Button,
    Dialog,
    InlineMessage,
    Calendar,
    //InputNumber,
    //TabView,
